sysauto_script.script:
// Retrieve the value of the x_58872_needit.autoCloseOverdue Application Property
var howLongToClose = gs.getProperty('x_58872_needit.autoCloseOverdue');

// gs.info("The NeedIt Application Prop autoCloseOverdue = " + howLongToClose);
// test for Script, I had quotes in the GR call around the "gs.daysAgo(howLongToClose)"
// didn't need them.


// Query the database for NeedIt Task records with Due date field
// values older than the number of days set in the x_58872_needit.autoCloseOverdue
// Application Property.  Only return NeedIt Task records that have a State Field value of
// Open or Work in progress.  Close the NeedIt Tasks and update the NeedIt Task Work Notes.

var gr = new GlideRecord("x_58872_needit_needit_task");
gr.addQuery("due_date","<=", gs.daysAgo(howLongToClose));
gr.addQuery("state", '<', 3);

gr.query();

// Write a log message for each long overdue NeedIt Task Record.
// Add a note to the Work notes field.  Change the State to Closed Incomplete.
// Update the NeedIt Task record.

while(gr.next()) {
	gs.info("Auto close NeedIt Task record = " + gr.number);
	gr.work_notes = "Record closed because it was too far past the Due Date.";
	gr.state = 4;
	gr.update();
}
